document.addEventListener("DOMContentLoaded",(()=>{const e=document.getElementById("color-picker");Object.entries({light:{name:"Light Theme",preview:"#ffffff"},dark:{name:"Dark Theme",preview:"#15202b"}}).forEach((([t,n])=>{const a=document.createElement("button");a.className="flex items-center w-full p-2 hover:bg-gray-100 rounded mb-2",a.innerHTML=`\n            <span class="flex-1">${n.name}</span>\n            <div class="w-4 h-4 rounded" style="background-color: ${n.preview}"></div>\n        `,a.addEventListener("click",(()=>{chrome.storage.sync.set({activeTheme:t}),chrome.tabs.query({active:!0,currentWindow:!0},(e=>{chrome.tabs.sendMessage(e[0].id,{type:"themeChange",theme:t})}))})),e.appendChild(a)})),chrome.storage.sync.get(["activeTheme"],(e=>{e.activeTheme}))}));